import React from "react";

// data
import matches from '../../data/matches.json';

class SeasonDetails extends  React.Component{

  componentDidMount() {
    const result = matches.filter((match) => match.Season_Id === this.props.Season_Id)
    console.log(result)
    this.setState({fixtures : result })
  }


  render() {
    const fixtures = this.state.fixtures;

    const fixtureList = (fixtures) ? (console.log(fixtures)) : (<p>No fixtures available ...</p>)

    return(
      <div id={`${this.props.Season_Id}`}>
        <h5>Fixtures for season {this.props.Season_Id}</h5>
        {fixtureList}
      </div>
    )
  }
}
export default SeasonDetails;
