import React from 'react';
import players from '../../data/players.json'

class BallByBall extends React.Component{

  state ={

  }

  componentDidMount() {
    const ballByBall = this.props.ballByBall;
    const firstInnings = ballByBall.filter((balls) => balls.Innings_Id === "1");
    const secondInnings = ballByBall.filter((balls) => balls.Innings_Id === "2");
    this.setState({firstInnings, secondInnings, players})
  }


  render() {
    const players = this.state.players;
    const firstInnings = this.state.firstInnings;
    const secondInnings = this.state.secondInnings;
    console.log(firstInnings);

    const renderFirstInnings = (firstInnings) ? (firstInnings.map((ball, index) => {
      if(ball.Batsman_Scored == "6"){
        return(
          <li className="collection-item">
            <div className="green lighten-3 ballData">
              <div className="title">{parseInt(ball.Over_Id) - 1}.{parseInt(ball.Ball_Id)}</div>
              <div>{players[parseInt(ball.Bowler_Id) - 1].Player_Name} to {players[parseInt(ball.Striker_Id) - 1].Player_Name} <br /> SIX</div>
            </div>
          </li>
        )
      }
      else{
        return(
          <li className="collection-item ">
            <div className='ballData'>
              <div className="title">{parseInt(ball.Over_Id) - 1}.{parseInt(ball.Ball_Id)}</div>
              <div>{players[parseInt(ball.Bowler_Id) - 1].Player_Name} to {players[parseInt(ball.Striker_Id) - 1].Player_Name}</div>
            </div>
          </li>
        )
      }

  })) : (<p>Loading first innings...</p>)


    return(
      <div>

        <div className="row">
          <div className="col m6">
            <ul className="collection">
              {renderFirstInnings}
            </ul>
          </div>
        </div>


      </div>
    )
  }
}
export default BallByBall;
