import React from 'react'
import SeasonCard from "./SeasonCard";
import Navbar from "../Navbar/Navbar";
import SeasonTab from "./SeasonTab";

// data
import seasons from '../../data/seasons.json';
import SeasonFixtures from "./SeasonFixtures";

class AllSeasonsList extends React.Component {
  state = {

  }

  componentDidMount() {
    this.setState({seasons})
  }


  render() {
    const seasons = this.state.seasons;
    const renderSeasonsTab = (seasons) ? (seasons.map((season) => {
      return(
        <SeasonTab Season_Id={season.Season_Id} Season_Year={season.Season_Year} />
      )
    }) ) : ( <p>Loading seasons tab ... </p> )

    const renderSeasonFixture = (seasons) ? (seasons.map((season) => {
      return(
        <SeasonFixtures Season_Id={season.Season_Id} />
      )
    })) : (<p>Loading fixtures for the season ...</p>)


    return(
      <div>
        <Navbar />
        <div className="container">
          <h3 className="indigo-text text-darken-4 darken">Season Fixtures</h3>
          <div className="divider"></div>
          <div className="row center">
            <div className="col l12 m12 s12">
              <ul className="tabs center-align">
                {renderSeasonsTab}
              </ul>
            </div>
            <div className="col l12 m12 s12">

            </div>
          </div>
        </div>
      </div>
    )
  }
}
export default AllSeasonsList;
