import React from 'react';

// data
import players from '../../data/players.json';
import seasons from '../../data/seasons.json';


class AllStatsList extends React.Component {
  state = {

  }

  componentDidMount() {
    this.setState({ players, seasons})
  }


  render() {
    const seasons = this.state.seasons;
    const players = this.state.players;
    console.log(seasons);
    console.log(players)
    const renderSeasonStats = (seasons) ? (seasons.map((season, index) => {
      return(
        <div className="row">
          <h5>Stats from season {season.Season_Id}, {season.Season_Year}</h5>
          <div className="col m6 s12">
            <div className="card z-depth-0">
              <div className="card-content">
                <p className="flow-text">Best Batsman</p>
                <h4>{players[season.Orange_Cap_Id + 1].Player_Name}</h4>
              </div>
            </div>
          </div>
        </div>
      )
    })) : (<p>Loading stats ...</p>)

    return(
      <div>
        {renderSeasonStats}
      </div>
    )
  }
}
export default AllStatsList;
