import React from "react";

// data
import matches from '../../data/matches.json';
import teams from '../../data/teams.json'

class SeasonDetails extends  React.Component{
    state = { }

  componentDidMount() {
    const seasonFixtures = matches.filter((match) => match.Season_Id.toLocaleString() === this.props.match.params.id)
    console.log(seasonFixtures)
    this.setState({seasonFixtures});
  }


  render() {
    const seasonFixtures = this.state.seasonFixtures;

    const renderSeasonFixtures = (seasonFixtures) ? (seasonFixtures.map((fixture) => {
      return(
        <div className="card fixtureCard z-depth-0">
          <img src={teams[fixture.Opponent_Team_Id - 1].Img_Src} height="100px" width="100px" alt="" />
          <div className="card-content">
            {/*<p className="center">Match played on {fixture.Match_Date}</p>*/}
            <div className="matchDetailShort">
              <div className="center">
                <h3><img style={{float: "left", paddingLeft: "10px"}} className="team-avatar" src={teams[fixture.Team_Name_Id - 1].Img_Src} height="100" alt="" /> {teams[fixture.Team_Name_Id - 1].Team_Name}</h3>
              </div>
              <div>
                <h4></h4>
                <h4>{teams[fixture.Opponent_Team_Id - 1].Team_Name}</h4>
              </div>
            </div>
          </div>
        </div>
      )
    })) : (<p>Loading fixtures ...</p>)

    return(
      <div className="container">
        <h5>Fixtures for season {this.props.Season_Id}</h5>
        {renderSeasonFixtures}
      </div>
    )
  }
}
export default SeasonDetails;
