import React from "react";
import axios from "axios";
import {Link} from 'react-router-dom';

class Tweets extends React.Component{
  state = {

  }

  componentDidMount() {
    axios.get("http://localhost:5000/ipl-tweets")
      .then((res) => {
        this.setState({tweets: res.data});
      })
      .catch((err) => console.log(err));
  }

  render() {
    const tweets = this.state.tweets;
    console.log(tweets)

    const renderTweets = (tweets) ? (tweets.slice(0,6).map((tweet, index) => {
      return(
        <a href={tweet.user.entities.url} rel="noopener noreferrer" target="_blank">
        <div className='col m4 s12'>
          <div style={{height: "200px"}} className="card-panel">
            <h6>{tweet.user.name}</h6>
            {tweet.text}
          </div>
        </div>
        </a>
      )
    })) : (<p>Loading tweets</p>)

    return(
      <div className="row">
        <p className="grey-text"><i><b>Latest Tweets</b></i></p>
        {renderTweets}
      </div>
    )
  }

}
export default Tweets;
